import "./ChartBar.css";

const ChartBar = (props) => {
  let heightValue = "0%";
  if (props.maxValue > 0) {
    heightValue = Math.round((props.value / props.maxxValue) * 100) + "%";
  }
  return (
    <div className="chart-bar">
      <div className="chart-bar__inner">
        <div className="chart-bar__fill" style={{ height: heightValue }}></div>
      </div>
      <div className="chart-bar__label">{props.label}</div>
    </div>
  );
};

export default ChartBar;
